'use strict';

// time set in seconds

var ONE_MINUTE = 60;
var TEN_MINUTES = 600;
var ONE_HOUR = 3600;
var ONE_DAY = 86400;
var ONE_WEEK = 604800;
var ONE_MONTH = 2592000; // 30 days
var ONE_YEAR = 31536000;

/**
 * @module timeValues
 * @type {Object}
 */
module.exports = Object.freeze({
  ONE_MINUTE: ONE_MINUTE,
  TEN_MINUTES: TEN_MINUTES,
  ONE_HOUR: ONE_HOUR,
  ONE_DAY: ONE_DAY,
  ONE_WEEK: ONE_WEEK,
  ONE_MONTH: ONE_MONTH,
  ONE_YEAR: ONE_YEAR
});